Ext.define('Evcorp.view.salesOrder.MortgageWinViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.mortgageWin',
    onMortgageSaveClick:function(){
		var form = this.lookupReference('mortgageWinForm');
		var win = this.getView();
		var isValid = form.getForm().isValid();
		if (isValid) {
			form.getForm().submit({
				success : function(form, action) {
					Ext.Msg.alert("提示", "保存成功");
					win.close();
				},
				failure : function(form, action) {
					Ext.Msg.alert("提示", "系统错误，保存失败");
					win.close();
				}
			})
		} else {
			Ext.Msg.alert("提示", '请把信息填完整');
		}
    }
});
